          "langVersion": "Pinyin",
          "textLines"  : [
            {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/1.mp3",
              "text"     : "Wǒ chángshì yī zhǒng xīn de yǐnshí xíguàn."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/2.mp3",
              "text"     : "Wǒ zhàn zài tǐzhòng jì shàng."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/3.mp3",
              "text"     : "Wǒ wéi zēngjiā xīnchéndàixiè zuòchū nǔlì."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/4.mp3",
              "text"     : "Wǒ měi zhōu duànliàn liǎng cì."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/5.mp3",
              "text"     : "Wǒ měitiān zuò yǎngwò qǐ zuò."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/6.mp3",
              "text"     : "Wǒ měitiān zuò fǔwòchēng."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/7.mp3",
              "text"     : "Wǒ zuò jiànkāng jiǎnchá."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/8.mp3",
              "text"     : "Wǒ huí dàojiā hòu shù kǒu."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/9.mp3",
              "text"     : "Wǒ xiǎng jǐnliàng bùdé gǎnmào."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/10.mp3",
              "text"     : "Wǒ jǐnliàng duō chī shūcài."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/11.mp3",
              "text"     : "Wǒ chī fù hán kuàng wùzhí de shíwù."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/12.mp3",
              "text"     : "Wǒ shèqǔ dàliàng de wéishēngsù."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/13.mp3",
              "text"     : "Wǒ quèbǎo chī dàliàng de shíwù xiānwéi."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/14.mp3",
              "text"     : "Wǒ jiǎnshǎo shèqǔ tànshuǐ huàhéwù."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/15.mp3",
              "text"     : "Wǒ chī fù hán dànbáizhí de shíwù."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/16.mp3",
              "text"     : "Wǒ pǎo shàng lóutī."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/17.mp3",
              "text"     : "Wǒ chángshì chī yíngyǎng jūnhéng de yǐnshí."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/18.mp3",
              "text"     : "Wǒ jìsuàn kǎlùlǐ."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/19.mp3",
              "text"     : "Wǒ shǎo chī tiánshí."
            }, {
              "audioLink": "https://www.chineseclass101.com/learningcenter/audio/transcript_1633/20.mp3",
              "text"     : "Wǒ jīng bù zhù yòuhuò."
            }
          ]
